#include <iostream>
#include <vector>
#include <cstring>
using namespace std;
class Solution{
    public:
    vector<vector<int>> pair;
    int dp[1005];
    int findLongestChain(vector<vector<int>>& pairs){
        memset(dp, -1, sizeof(dp));
        pair = pairs;
        int max1 = 1;
        for(int i = 0; i < pairs.size(); i++){
            int a = f(i) ;
            dp[i] = a;
            if(a > max1){
                max1 = a;
            }
        }
        return max1;
    }
    int f(int index){
        if(dp[index] != -1){
            cout << "used" << endl;
            return dp[index];
        }
        int maxx = 1;
        for(int i = 0; i < pair.size(); i++){
            int a = 0;
            if(index != i){
                if(pair[index][1] < pair[i][0]){
                    a = f(i) + 1;
                    dp[i] = a-1;
                }
                if(a > maxx){
                    maxx = a;
                }
            }
        }
        dp[index] = maxx;
        return maxx;
    }
};
