#include <string>
#include <iostream>
#include <vector>
using namespace std;
class Solution{
    public:
    string glo;
    vector<vector<int>> dp;
    int fun(bool flag, int index){
        if(dp[index][flag] != -1){
            return dp[index][flag];
        }
        if(index == glo.length()){
            return 0;
        }
        if(flag == true){
            int b;
            if(glo[index] == 'a'){
                b = fun(true, index + 1) + 1;
            }
            else{
                b = fun(true, index +1);
            }
            int set = flag;
            dp[index][flag] = b;
            return b; 
        }
        else{
            int a;
            int b;
            if(glo[index] == 'a'){
                a = fun(false, index + 1);
                b = fun(true, index + 1) + 1;
            }
            else{
                a = fun(false, index + 1) + 1;
                b = fun(true, index + 1);
            }
            int set = flag;
            return dp[index][set] =  min(a, b);
        }
    }
    int minimumDeletions(string s){
        dp.resize(100005, vector<int>(2, -1));
        glo = s;
        int n = s.size();
        char r = s[0];
        int a;
        int b;
        if(r == 'a'){
            a = fun(false, 1);
            b = fun(true, 1) + 1;
        }
        else{
            a = fun(false, 1) + 1;
            b = fun(true, 1);
        }
        return min(a,b);
    }
};
int main(){
    Solution s;
    int a = s.minimumDeletions("");
    cout << a << endl;
    return 0;
}